import React, { useState, useEffect } from 'react';

interface Position {
  row: number;
  col: number;
}

interface Cell {
  letter: string;
  selected: boolean;
  isPartOfHint?: boolean;
}

interface WordGridProps {
  words: string[];
  onWordFound: (word: string) => void;
  hintedWord?: string;
}

const WordGrid: React.FC<WordGridProps> = ({ words, onWordFound, hintedWord }) => {
  const gridSize = 15;
  const [grid, setGrid] = useState<Cell[][]>(createInitialGrid());

  const placeWordInGrid = (grid: Cell[][], word: string, x: number, y: number, direction: number): void => {
    const dx = direction === 0 ? 1 : direction === 2 ? 1 : 0;
    const dy = direction === 1 ? 1 : direction === 2 ? 1 : 0;

    for (let i = 0; i < word.length; i++) {
      const currentX = x + dx * i;
      const currentY = y + dy * i;
      grid[currentY][currentX] = {
        letter: word[i],
        selected: false,
        isPartOfHint: false
      };
    }
  };

  useEffect(() => {
    const newGrid = createInitialGrid();
    words.forEach(word => {
      let placed = false;
      let attempts = 0;
      while (!placed && attempts < 100) {
        const direction = Math.floor(Math.random() * 3);
        const startX = Math.floor(Math.random() * (gridSize - word.length));
        const startY = Math.floor(Math.random() * (gridSize - word.length));

        if (canPlaceWord(newGrid, word, startX, startY, direction)) {
          placeWord(newGrid, word, startX, startY, direction);
          placed = true;
        }
        attempts++;
      }
    });
    setGrid(newGrid);
  }, [words]);

  useEffect(() => {
    if (hintedWord) {
      const newGrid = grid.map(row => 
        row.map(cell => ({ ...cell, isPartOfHint: false }))
      );

      let found = false;
      for (let y = 0; y < gridSize && !found; y++) {
        for (let x = 0; x < gridSize && !found; x++) {
          const directions = [[1, 0], [0, 1], [1, 1]];

          for (const [dx, dy] of directions) {
            if (checkWordAtPosition(x, y, hintedWord, dx, dy)) {
              for (let i = 0; i < hintedWord.length; i++) {
                const currentX = x + dx * i;
                const currentY = y + dy * i;
                newGrid[currentY][currentX].isPartOfHint = true;
              }
              found = true;
              break;
            }
          }
        }
      }
      setGrid(newGrid);
    }
  }, [hintedWord]);

  const checkWordAtPosition = (startX: number, startY: number, word: string, dx: number, dy: number): boolean => {
    if (startX + dx * (word.length - 1) >= gridSize || 
        startY + dy * (word.length - 1) >= gridSize) {
      return false;
    }

    for (let i = 0; i < word.length; i++) {
      const x = startX + dx * i;
      const y = startY + dy * i;
      if (grid[y][x].letter !== word[i]) {
        return false;
      }
    }
    return true;
  };

  const markHintedWord = (startX: number, startY: number, word: string) => {
    const directions = [
      [0, 1],  // horizontal
      [1, 0],  // vertical
      [1, 1],  // diagonal
    ];

    for (const [dx, dy] of directions) {
      let matches = true;
      for (let i = 0; i < word.length; i++) {
        const x = startX + dx * i;
        const y = startY + dy * i;
        if (x >= gridSize || y >= gridSize || grid[y][x].letter !== word[i]) {
          matches = false;
          break;
        }
      }
      if (matches) {
        for (let i = 0; i < word.length; i++) {
          const x = startX + dx * i;
          const y = startY + dy * i;
          grid[y][x].isPartOfHint = true;
        }
        return;
      }
    }
  };

  function createInitialGrid(): Cell[][] {
    return Array(gridSize).fill(null).map(() =>
      Array(gridSize).fill(null).map(() => ({
        letter: String.fromCharCode(65 + Math.floor(Math.random() * 26)),
        selected: false
      }))
    );
  }

  const canPlaceWord = (grid: Cell[][], word: string, x: number, y: number, direction: number): boolean => {
    const dx = direction === 0 ? 1 : direction === 2 ? 1 : 0;
    const dy = direction === 1 ? 1 : direction === 2 ? 1 : 0;

    if (x + dx * word.length > gridSize || y + dy * word.length > gridSize) {
      return false;
    }

    for (let i = 0; i < word.length; i++) {
      const currentX = x + dx * i;
      const currentY = y + dy * i;
      const currentCell = grid[currentY][currentX];
      if (currentCell.letter !== word[i] && currentCell.letter !== '') {
        return false;
      }
    }
    return true;
  };

  const placeWord = (grid: Cell[][], word: string, x: number, y: number, direction: number): void => {
    const dx = direction === 0 ? 1 : direction === 2 ? 1 : 0;
    const dy = direction === 1 ? 1 : direction === 2 ? 1 : 0;

    for (let i = 0; i < word.length; i++) {
      const currentX = x + dx * i;
      const currentY = y + dy * i;
      grid[currentY][currentX].letter = word[i];
    }
  };

  const [selection, setSelection] = useState<Position[]>([]);

  const handleCellClick = (rowIndex: number, colIndex: number): void => {
    const newSelection = [...selection, { row: rowIndex, col: colIndex }];
    setSelection(newSelection);

    const selectedWord = newSelection
      .map(pos => grid[pos.row][pos.col].letter)
      .join('');

    if (words.includes(selectedWord)) {
      onWordFound(selectedWord);
      setSelection([]);
    } else if (newSelection.length >= 10) { // Reset if selection is too long
      setSelection([]);
    }
  };

  return (
    <div className="word-grid">
      <div className="grid-container">
        {grid.map((row, rowIndex) => (
          <div key={rowIndex} className="grid-row">
            {row.map((cell, colIndex) => (
              <div
                key={`${rowIndex}-${colIndex}`}
                className={`grid-cell ${
                  selection.some(pos => pos.row === rowIndex && pos.col === colIndex)
                    ? 'selected'
                    : ''
                } ${
                  hintedWord && cell.isPartOfHint ? 'hinted' : ''
                }`}
                onClick={() => handleCellClick(rowIndex, colIndex)}
              >
                {cell.letter}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default WordGrid;
